function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{s.classList.remove("active"),e.classList.remove("active"),c.classList.remove("locked")}))}burgerMenu();